const path = require('path');

const context = process.cwd();

const paths = {
  '$components/Stepper': path.resolve(context, 'src/js/components/Stepper'),
  '$containers/Main': path.resolve(context, 'src/js/containers/Main'),
  '$redux/store': path.resolve(context, 'src/js/redux/store'),
  '$config': path.resolve(context, 'src/js/config'),
  '$config/settings': path.resolve(context, 'src/js/config/settings')
};

class Compilator {
  constructor() {
    this.plugins = [];
    this.request = null;
  }

  plugin(source, plugin) {
    this.plugins.push(plugin);
  }

  doResolve(target, request, notice, callback) {    
    callback(null, request);
  }
}

module.exports = { paths, Compilator };